import styled from "styled-components";
import Row from "../Components/Layout/Row";
import Column from "../Components/Layout/Column";
import Form from "../Components/Form/Form";
import FormInput from "../Components/Form/FormInput";

const FormContainer = styled.div`
  position: fixed;
  left: 0px;
`;

const Contact = () => {
  const { t } = useTranslation();

  const title = <h1>{t("Home.Contact.Title")}</h1>;

  const sendButtonText = <p>{t("Home.Contact.SendNow")}</p>;

  return (
    <FormContainer>
      <Row marginTop="80px" alignItems="flex-start">
        <Column width="50%">
          <Form title={title} sendButtonText={sendButtonText}>
            <FormInput
              placeholder={t("Home.Contact.Company")}
              type="text"
              name="name"
              marginTop="60px"
              marginBottom="20px"
              width="90%"
            />
            <FormInput
              placeholder={t("Home.Contact.ContactPerson")}
              type="text"
              name="contactPerson"
              marginBottom="20px"
              width="90%"
            />
            <FormInput
              placeholder={t("Home.Contact.EMail")}
              type="email"
              name="email"
              marginBottom="20px"
              width="90%"
            />
            <FormInput
              placeholder={t("Home.Contact.Message")}
              name="message"
              type="textarea"
              marginBottom="45px"
              width="90%"
              multiline
            />
          </Form>
        </Column>
        <Column width="50%" widthTablet="100%">
          <p>Image</p>
        </Column>
      </Row>
    </FormContainer>
  );
};

export default Contact;
