import React, { useState } from "react";
import styled from "styled-components";
import { useTranslation } from "react-i18next";
import RadioButton from "../Components/Radiobutton/RadioButton";
import Carousel from "../Components/Carousel/Carousel";
import Row from "../Components/Row";
import Column from "../Components/Column";
import Text from "../Components/Text";

const HomeContainer = styled.div`
  padding: 0.5rem calc((100vw - 1000px) / 2);
  padding-top: 200px;
`;

const items = [
  <Row marginTop="20px">
    <Column>
      <Text>sec 1</Text>
    </Column>
    <Column>
      <Text>sec 2</Text>
    </Column>
  </Row>,
  <Row marginTop="20px">
    <Column>
      <Text>sec 3</Text>
    </Column>
    <Column>
      <Text>sec 4</Text>
    </Column>
  </Row>,
  <Row marginTop="20px">
    <Column>
      <Text>sec 5</Text>
    </Column>
    <Column>
      <Text>sec 6</Text>
    </Column>
  </Row>,
];

const responsive = {
  0: { items: 1 },
  568: { items: 1 },
  1024: { items: 1 },
  1440: { items: 1 },
  1920: { items: 1 },
};

const Home = () => {
  const { t } = useTranslation();
  const [radioOption, setRadioOption] = useState("one");

  const handleRadioChange = (value) => {
    setRadioOption(value);
  };

  return (
    <HomeContainer>
      <RadioButton
        id="one"
        name="radioOption"
        label={t("Home.Radiobutton.Option1")}
        checked={radioOption === "one"}
        onChange={handleRadioChange}
      />
      <RadioButton
        id="two"
        name="radioOption"
        label={t("Home.Radiobutton.Option2")}
        checked={radioOption === "two"}
        onChange={handleRadioChange}
      />
      <p>{radioOption + " selected"}</p>
      <Carousel items={items} responsive={responsive} />
    </HomeContainer>
  );
};

export default Home;
