import React, { useState } from "react";
import styled from "styled-components";
import { useTranslation } from "react-i18next";
import RadioButton from "../Components/Radiobutton/RadioButton";
import Carousel from "../Components/Carousel/Carousel";
import Row from "../Components/Row";
import Column from "../Components/Column";
import Text from "../Components/Text";
import Divider from "../Components/Divider";

const HomeContainer = styled.div`
  padding: 0.5rem calc((100vw - 1000px) / 2);
  padding-top: 200px;
`;

const responsive = {
  0: { items: 1 },
  568: { items: 1 },
  1024: { items: 1 },
  1440: { items: 1 },
  1920: { items: 1 },
};

const Home = () => {
  const { t } = useTranslation();
  const [radioOption, setRadioOption] = useState("one");

  const handleRadioChange = (value) => {
    setRadioOption(value);
  };

  const items = [
    <Row marginTop="20px">
      <Text
        size="36px"
        sizeTablet="25px"
        sizeMobile="17px"
        marginLeft="20px"
        color="#000000"
        transform="uppercase"
        align="left"
        lineHeightMobile="2"
      >
        {t("Home.Carousel.Title")}
      </Text>
      <Divider width="97%" />
      <Column width="40%">
        <Text>sec 1</Text>
      </Column>
      <Column width="40%">
        <Text>sec 2</Text>
      </Column>
    </Row>,
    <Row marginTop="20px">
      <Column>
        <Text>sec 3</Text>
      </Column>
      <Column>
        <Text>sec 4</Text>
      </Column>
    </Row>,
    <Row marginTop="20px">
      <Column>
        <Text>sec 5</Text>
      </Column>
      <Column>
        <Text>sec 6</Text>
      </Column>
    </Row>,
  ];

  return (
    <HomeContainer>
      <RadioButton
        id="one"
        name="radioOption"
        label={t("Home.Radiobutton.Option1")}
        checked={radioOption === "one"}
        onChange={handleRadioChange}
      />
      <RadioButton
        id="two"
        name="radioOption"
        label={t("Home.Radiobutton.Option2")}
        checked={radioOption === "two"}
        onChange={handleRadioChange}
      />
      <p>{radioOption + " selected"}</p>
      <Carousel items={items} responsive={responsive} />
    </HomeContainer>
  );
};

export default Home;
